CREATE TABLE `weight_tree` (
  `group_id` int NOT NULL COMMENT '组ID，用于区分不同的树',
  `id` int NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `name` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '维度名称',
  `parent_id` int DEFAULT '0' COMMENT '父节点ID，0表示根节点',
  `level` int NOT NULL COMMENT '层级：1-一级维度，2-二级维度，3-三级维度',
  `weight` decimal(5,4) DEFAULT NULL COMMENT '权重值，如0.25、0.5等',
  `full_path` varchar(500) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '完整路径，格式：父ID/父ID/...',
  `sort_order` int DEFAULT '0' COMMENT '同级排序顺序',
  `create_by` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '记录创建人',
  `create_time` timestamp NULL DEFAULT NULL COMMENT '记录创建时间',
  `update_by` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '记录更新人',
  `update_time` timestamp NULL DEFAULT NULL COMMENT '记录更新时间',
  PRIMARY KEY (`id`,`group_id`) USING BTREE,
  UNIQUE KEY `uk_group_parent_name` (`group_id`,`parent_id`,`name`),
  KEY `idx_group_id` (`group_id`),
  KEY `idx_parent_id` (`parent_id`),
  KEY `idx_level` (`level`),
  KEY `idx_full_path` (`full_path`(255)),
  KEY `idx_sort_order` (`sort_order`)
) ENGINE=InnoDB AUTO_INCREMENT=79 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='维度树结构表';

